<?phpnamespace Lahaina\CMS\Controller\Backend;use \Lahaina\CMS\Controller\BackendController;use \Lahaina\Framework\Persistence\Caching\AbstractCache;use \Lahaina\Helper\Alert\SuccessAlert;use \Lahaina\Helper\Alert\WarningAlert;class MaintenanceController extends BackendController{    public function indexAction($args)    {        $this->view->setTitle('Maintenance');        return $this->render('backend/maintenance/index');    }    public function deleteCacheAction($args)    {        $cacheTag = $this->getRequest()->getPost('cache');        $cache = $this->app()->get('cache');        if ($cacheTag) {            if ($cacheTag === 'all') {                $cache->clear();            } else {                $cache->deleteByTag($cacheTag);            }            $alert = new SuccessAlert('Cache successful cleared');        } else {            $alert = new WarningAlert('No cache selected');        }        $this->getSession()->setFlash('alert', $alert);        return $this->redirectToRoute('maintenance_index');    }}