<?phpnamespace Lahaina\CMS\Model;use \Lahaina\CMS\Handler\Config;use \Lahaina\Framework\Core\AbstractModel;use \Lahaina\Framework\Persistence\ORM;class ModuleModel extends AbstractModel{    /**     * @var string     */    public static $tableName = 'modules';    /**     * @var string     */    public static $primaryKey = 'module_id';    /**     * @var array     */    public static $properties = ['module_id', 'name', 'folder'];    /**     * Get module.     *     * @return ORM     */    public function section()    {        return $this->belongsTo('\\WebsiteBaker\\Models\\Section', 'section_id');    }    /**     * Render module frontend.     *     * @return string     */    public function render($view)    {        $moduleFilePath = $this->getPath('/frontend.php');        return $view->renderFile($moduleFilePath, array(                'app' => $this->app(),        ));    }    /**     * Get module url.     *     * @param string $uri     *     * @return string     */    public function getUrl($uri = '')    {        return $this->getConfig()                ->getModulesUrl('/' . $this->folder . '/' . $uri);    }    /**     * Get module path.     *     * @param string $uri     *     * @return string     */    public function getPath($uri = '')    {        return $this->getConfig()                ->getModulesPath('/' . $this->folder . '/' . $uri);    }    /**     * Get config     *     * @return Config     */    public function getConfig()    {        return $this->app()->get('config');    }}