<?phpnamespace Lahaina\CMS\Model;use Lahaina\Framework\Core\AbstractModel;use Lahaina\Framework\Persistence\ORM;use Lahaina\Framework\Handler\Validation\Validator;class SettingModel extends AbstractModel{    /**     * @var string     */    public static $tableName = 'settings';    /**     * @var string     */    public static $primaryKey = 'setting_id';    /**     * @var array     */    public static $properties = ['setting_id', 'website_title', 'website_description',        'keywords', 'author', 'theme_id',        'backend_theme_id', 'language_id'];    /**     * Get the ORM to find the frontend theme     *     * @return ORM     */    public function theme()    {        return $this->belongsTo('\\Lahaina\\CMS\\Model\\ThemeModel', 'theme_id');    }    /**     * Get the ORM to find the backend theme     *     * @return ORM     */    public function backendTheme()    {        return $this->belongsTo('\\Lahaina\\CMS\\Model\\ThemeModel', 'backend_theme_id');    }    /**     * Get the ORM to find the language     *     * @return ORM     */    public function language()    {        return $this->belongsTo('\\Lahaina\\CMS\\Model\\LanguageModel', 'language_id');    }    /**     * Save setting entity     * @return boolean     */    public function save()    {        if ($this->validate()) {            return parent::save();        }        return false;    }    /**     * Validate setting entity     *     * @return bool     */    public function validate()    {        $validator = new Validator($this->toArray());        $validator            ->required()            ->betweenLength(3, 50)            ->set('website_title', 'Website title');        $validator            ->maxlength(150)            ->set('website_description', 'Website description');        $validator            ->maxlength(255)            ->set('keywords', 'Keywords');        $validator            ->maxlength(50)            ->set('author', 'Author');        return (bool) $validator->validate();    }}