<?phpnamespace Neoflow\CMS\Controller\Backend;use \Neoflow\CMS\Controller\BackendController;use \Neoflow\CMS\Model\PermissionModel;use \Neoflow\Framework\HTTP\Responsing\Response;class RoleController extends BackendController{    /**     * Constructor     */    public function __construct()    {        parent::__construct();        $this->view            ->setTitle('Accounts')            ->setSubtitle('Roles');    }    /**     * Index action     *     * @param array $args     * @return Response     */    public function indexAction($args)    {        // Get permissions        $permissions = PermissionModel::findAll();        return $this->render('backend/role/index', array(                'permissions' => $permissions,        ));    }}