<?phpnamespace Neoflow\CMS\Model;use Neoflow\Framework\ORM\AbstractEntityModel;class PermissionModel extends AbstractEntityModel{    /**     * @var string     */    public static $tableName = 'permissions';    /**     * @var string     */    public static $primaryKey = 'permission_id';    /**     * @var array     */    public static $properties = ['permission_id', 'title', 'description', 'tag'];    public function roles()    {        return $this->hasManyThrough('\\Neoflow\\CMS\\Model\\RoleModel', '\\Neoflow\\CMS\\Model\\RolePermissionModel', 'permission_id', 'role_id');    }    /**     * Get translated title.     *     * @return string     */    public function getTranslatedTitle()    {        return $this->app()->get('translator')->translate($this->title);    }    /**     * Get translated title.     *     * @return string     */    public function getTranslatedDescription()    {        return $this->app()->get('translator')->translate($this->description);    }}