<?phpnamespace Neoflow\CMS\Model;use Neoflow\Framework\Core\AbstractModel;class RoleModel extends AbstractModel{    /**     * @var string     */    public static $tableName = 'roles';    /**     * @var string     */    public static $primaryKey = 'role_id';    /**     * @var array     */    public static $properties = ['role_id', 'title', 'description'];    public function permissions()    {        return $this->hasManyThrough('\\Neoflow\\CMS\\Model\\PermissionModel', '\\Neoflow\\CMS\\Model\\RolePermissionModel', 'role_id', 'permission_id');    }}