<?phpnamespace Neoflow\CMS\Model;use Neoflow\Framework\Core\AbstractModel;class RoleModel extends AbstractModel{    /**     * @var string     */    public static $tableName = 'roles';    /**     * @var string     */    public static $primaryKey = 'role_id';    /**     * @var array     */    public static $properties = ['role_id', 'title', 'description'];    public function permissions()    {        return $this->hasManyThrough('\\Neoflow\\CMS\\Model\\PermissionModel', '\\Neoflow\\CMS\\Model\\RolePermissionModel', 'role_id', 'permission_id');    }    public static function create($data)    {        $role = parent::create($data);        if ($role && $role->permission_ids) {            foreach ($role->permission_ids as $permission_id) {                PermissionModel::create(array(                    'role_id' => $role->id(),                    'permission_id' => $permission_id                ));            }        }        return $role;    }}