<div class="row">    <div class="col-lg-8">        <div class="panel panel-default">            <div class="panel-heading">                <h3 class="panel-title">Logfile (last 100 lines)</h3>            </div>            <div class="panel-body">                <textarea rows="15" readonly id="logfileTextarea" class="form-control vresize"><?php                    $file = file($view->logger()->getLogFilePath());                    for ($i = max(0, count($file) - 101); $i < count($file); $i++) {                        echo $file[$i];                    }                    ?></textarea>                <script>                    var textarea = document.getElementById('logfileTextarea');                    textarea.scrollTop = textarea.scrollHeight;                </script>                <ul class="list-inline">                    <li>LogLevel: <?= $view->config()->get('logger')->get('level') ?></li>                    <li>LogFile: <?= str_replace($view->config()->getPath(), '...', $view->app()->get('logger')->getLogFilePath()) ?></li>                </ul>            </div>        </div>    </div>    <div class="col-lg-4">        <div class="panel panel-default">            <div class="panel-heading">                <h3 class="panel-title"><?= translate('Cache') ?></h3>            </div>            <div class="panel-body">                <p><?= translate('Please specify which cached data you want to clear') ?>:</p>                <form method="post" action="<?= generate_url('maintenance_delete_cache') ?>" class="form-horizontal">                    <div class="form-group">                        <div class="col-xs-12">                            <div class="radio">                                <label>                                    <input type="radio" name="cache" checked="" value="all"> <?= translate('All cached data') ?>                                </label>                            </div>                            <div class="radio">                                <label>                                    <input type="radio" name="cache" value="_query"> <?= translate('Only cached database results') ?>                                </label>                            </div>                            <div class="radio">                                <label>                                    <input type="radio" name="cache" value="_route"> <?= translate('Only cached application routes') ?>                                </label>                            </div>                        </div>                    </div>                    <div class="form-group">                        <div class="col-xs-12">                            <button type="submit" class="btn btn-primary"><i class="fa fa-cog"></i> <?= translate('Clear cache') ?></button>                        </div>                    </div>                </form>            </div>        </div>    </div></div>