<?= $this->renderTemplate('navbar-language', array('languages' => $languages, 'activeLanguage' => $activeLanguage)) ?><div class="row">    <div class="col-lg-8">        <div class="panel panel-default">            <div class="panel-heading">                <h3 class="panel-title">                    <?= $this->translate('All pages') ?>                    <span class="visible-xs pull-right">                        <?= $activeLanguage->renderFlagIcon() ?> <?= $activeLanguage->getTranslatedTitle() ?>                    </span>                </h3>            </div>            <div class="panel-body">                <?php if ($navitems) { ?>                    <div class="nestable" data-save-url="<?= $this->generateUrl('navigation_update_navitem_order') ?>" id="nestable">                        <?= $this->renderNavitemNestable($navitems) ?>                    </div>                <?php } else { ?>                    <p class="alert alert-warning"><?= $this->translate('Sorry, no page found') ?></p>                <?php } ?>            </div>        </div>    </div>    <div class="col-lg-4">        <div class="panel panel-default">            <div class="panel-heading">                <h3 class="panel-title"><?= $this->translate('New page') ?></h3>            </div>            <div class="panel-body">                <form method="post" action="<?= $this->generateUrl('page_create') ?>" class="form-horizontal">                    <input type="hidden" value="<?= $activeLanguage->id() ?>" name="language_id" />                    <div class="form-group">                        <label for="inputTitle" class="col-sm-3 control-label">                            <?= $this->translate('Title') ?>                        </label>                        <div class="col-sm-9">                            <input id="inputTitle" type="text" required class="form-control" name="title" maxlength="50" minlength="3" />                        </div>                    </div>                    <div class="form-group">                        <label for="selectPage" class="col-sm-3 control-label">                            <?= $this->translate('Top page') ?>                        </label>                        <div class="col-sm-9">                            <select class="form-control select2" name="parent_navitem_id" id="selectPage">                                <option value=""><?= $this->translate('None') ?></option>                                <?= $this->renderNavitemOptions($navitems) ?>                            </select>                        </div>                    </div>                    <div class="form-group">                        <div class="col-sm-offset-3 col-sm-9">                            <button type="submit" class="btn btn-primary"><i class="fa fa-fw fa-save"></i> <?= $this->translate('Create') ?></button>                        </div>                    </div>                </form>            </div>        </div>    </div></div>