panel-title"><?= $this->translate('New page') ?></h3>            </div>            <div class="panel-body">                <form method="post" action="<?= $this->generateUrl('page_create') ?>" class="form-horizontal">                    <input type="hidden" value="<?= $pageLanguage->id() ?>" name="language_id" />                    <div class="form-group <?= $this->hasValidationError('title', 'has-error') ?>">                        <label for="inputTitle" class="col-sm-3 control-label">                            <?= $this->translate('Title') ?>                        </label>                        <div class="col-sm-9">                            <input id="inputTitle" type="text" required class="form-control" name="title" maxlength="50" minlength="3" />                        </div>                    </div>                    <div class="form-group <?= $this->hasValidationError('module_id', 'has-error') ?>">                        <label for="selectModule" class="col-sm-3 control-label">                            <?= $this->translate('Module') ?>                        </label>                        <div class="col-sm-9">                            <select required class="form-control select2" name="module_id" id="selectModule" data-placeholder="">                                <?php                                foreach ($modules as $module) {                                    ?>                                    <option value="<?= $module->id() ?>"><?= $module->title ?></option>                                    <?php                                }                                ?>                            </select>                        </div>                    </div>                    <div class="form-group <?= $this->hasValidationError('visibility', 'has-error') ?>">                        <label for="selectVisibility" class="col-sm-3 control-label">                            <?= $this->translate('Visibility') ?>                        </label>                        <div class="col-sm-9">                            <select required class="form-control select2" name="visibility" id="selectVisibility">                                <option value="visible"><?= $this->translate('Visible') ?></option>                                <option value="restricted"><?= $this->translate('Restricted') ?></option>                                <option value="hidden"><?= $this->translate('Hidden') ?></option>                            </select>                        </div>                    </div>                    <div class="form-group <?= $this->hasValidationError('parent_navitem_id', 'has-error') ?>">                        <label for="selectPage" class="col-sm-3 control-label">                            <?= $this->translate('Top page') ?>                        </label>                        <div class="col-sm-9">                            <select class="form-control select2" name="parent_navitem_id" id="selectPage">                                <option value=""><?= $this->translate('None') ?></option>                                <?= $this->renderNavitemOptions($navitems) ?>                            </select>                        </div>                    </div>                    <div class="form-group">                        <div class="col-sm-offset-3 col-sm-9">                            <div class="checkbox">                                <label>                                    <input name="is_active" value="0" type="hidden" />                                    <input name="is_active" value="1" type="checkbox" checked /> <?= $this->translate('Page is active') ?>                                </label>                            </div>                        </div>                    </div>                    <div class="form-group">                        <div class="col-sm-offset-3 col-sm-9">                      