<form method="post" action="<?= $this->generateUrl('setting_save') ?>" class="form-horizontal">    <div class="row">        <div class="col-lg-8">            <div class="panel panel-default">                <div class="panel-heading">                    <h3 class="panel-title"><?= $this->translate('General settings') ?></h3>                </div>                <div class="panel-body">                    <div class="form-group <?= $this->hasValidationError('website_title', 'has-error') ?>">                        <label for="inputWebsiteTitle" class="col-sm-3 control-label">                            <?= $this->translate('Website title') ?>                        </label>                        <div class="col-sm-9">                            <input id="inputWebsiteTitle" type="text" required value="<?= $setting->website_title ?>" class="form-control" name="setting[website_title]" maxlength="50" minlength="3" />                        </div>                    </div>                    <div class="form-group <?= $this->hasValidationError('website_description', 'has-error') ?>">                        <label for="textareaWebsiteDescription" class="col-sm-3 control-label">                            <?= $this->translate('Website description') ?>                        </label>                        <div class="col-sm-9">                            <textarea name="setting[website_description]" class="form-control vresize" maxlength="150" id="textareaWebsiteDescription" rows="3"><?= $setting->website_description ?></textarea>                        </div>                    </div>                    <div class="form-group <?= $this->hasValidationError('keywords', 'has-error') ?>">                        <label for="inputKeywords" class="col-sm-3 control-label">                            <?= $this->translate('Keywords') ?>                        </label>                        <div class="col-sm-9">                            <input id="inputKeywords" type="text" value="<?= $setting->keywords ?>" class="form-control" name="setting[keywords]" maxlength="255" />                        </div>                    </div>                    <div class="form-group <?= $this->hasValidationError('Author', 'has-error') ?>">                        <label for="inputAuthor" class="col-sm-3 control-label">                            <?= $this->translate('Author') ?>                        </label>                        <div class="col-sm-9">                            <input id="inputAuthor" type="text" value="<?= $setting->author ?>" class="form-control" name="setting[author]" maxlength="50" />                        </div>                    </div>                    <hr />                    <div class="form-group">                        <label for="selectTheme" class="col-sm-3 control-label">                            <?= $this->translate('Frontend theme') ?>                        </label>                        <div class="col-sm-9">                            <select class="form-control select2" name="setting[theme_id]" id="selectTheme">                                <?php                                foreach ($themes as $theme) {                                    if ($theme->type === 'frontend') {                                        ?>                                        <option value="<?= $theme->id() ?>"  <?= ($setting->theme_id = $theme->id() ? 'selected' : '') ?>><?= $theme->title ?></option>                                        <?php                                    }                                }                                ?>                            </select>                        </div>                    </div>                    <div class="form-group">                        <label for="selectBackendTheme" class="col-sm-3 control-label">                            <?= $this->translate('Backend theme') ?>                        </label>                        <div class="col-sm-9">                            <select class="form-control select2" name="setting[backend_theme_id]" id="selectBackendTheme">                                <?php                                foreach ($themes as $theme) {                                    if ($theme->type === 'backend') {                                        ?>                                        <option value="<?= $theme->id() ?>"  <?= ($setting->theme_id = $theme->id() ? 'selected' : '') ?>><?= $theme->title ?></option>                                        <?php                                    }                                }                                ?>                            </select>                        </div>                    </div>                    <hr />                    <div class="form-group">                        <label for="selectDefaultLanguage" class="col-sm-3 control-label">                            <?= $this->translate('Default language') ?>                        </label>                        <div class="col-sm-9">                            <select class="form-control select2" name="setting[language_id]" id="selectDefaultLanguage">                                <?php foreach ($languages as $language) {                                    ?>                                    <option value="<?= $language->id() ?>"  <?= ($language->id() === $setting->language_id ? 'selected' : '') ?>><?= $language->getTranslatedTitle() ?></option>                                <?php }                                ?>                            </select>                        </div>                    </div>                    <div class="form-group">                        <label for="selectActiveLanguages" class="col-sm-3 control-label"><?= $this->translate('Languages') ?></label>                        <div class="col-sm-9">                            <select class="form-control select2" name="setting[language][active_language_ids][]" multiple id="selectActiveLanguages">                                <?php foreach ($languages as $language) {                                    ?>                                    <option value="<?= $language->id() ?>"  <?= ($language->is_active ? 'selected' : '') ?>><?= $language->getTranslatedTitle() ?></option>                                <?php }                                ?>                            </select>                        </div>                    </div>                    <div class="form-group">                        <div class="col-sm-offset-3 col-sm-9">                            <button type="submit" name="saveSubmit" class="btn btn-primary"><i class="fa fa-fw fa-floppy-o"></i> <?= $this->translate('Save') ?></button>                        </div>                    </div>                </div>            </div>        </div>        <div class="col-lg-4">            <div class="panel panel-default">                <div class="panel-heading">                    <h3 class="panel-title"><?= $this->translate('Advanced settings') ?></h3>                </div>                <div class="panel-body">                    <p>                        <?= $this->translate('Advanced settings description') ?>                    </p>                    <h4><?= $this->translate('Path of the config file') ?>:</h4>                    <ul>                        <li><i><?= $this->getConfig()->getPath('/config.php') ?></i></li>                    </ul>                </div>            </div>        </div>    </div></form>