<?php// Include framework functionsrequire_once 'functions/is_assoc.php';require_once 'functions/is_json.php';require_once 'functions/is_url.php';require_once 'functions/slugify.php';// Register autoload for application and framework classesspl_autoload_register(function ($className) {    $className = ltrim($className, '\\');    $className = str_replace('\\', '/', $className);    $directories = array(        'framework/classes/',        'application/classes/',    );    foreach ($directories as $directory) {        $classFile = $directory . $className . '.php';        if (is_file($classFile)) {            include_once $classFile;        }    }});