<?phpnamespace Lahaina\CMS\Handler;use Lahaina\CMS\Mapper\ModuleMapper;class Translator extends \Lahaina\Framework\Handler\Translator{    /**     * Load translation.     *     * @return self     */    public function loadTranslation()    {        parent::loadTranslation();        // Load translation files of modules        $moduleMapper = new ModuleMapper();        $modules = $moduleMapper->findAll();        foreach ($modules as $module) {            // Load translation file            $translationFile = $module->getPath('/i18n/' . $this->getCurrentLanguageCode() . '.php');            $this->runTranslationFile($translationFile);            // Load fallback translation file            $fallbackTranslationFile = $module->getPath('/i18n/' . $this->getDefaultLanguageCode() . '.php');            $this->runTranslationFile($fallbackTranslationFile, true);        }        return $this;    }}