<?phpnamespace Lahaina\Framework\HTTP\Responsing;class RedirectResponse extends Response{    public function __construct($url, $statusCode = 302)    {        $content = str_replace('[url]', htmlspecialchars($url, ENT_QUOTES, 'UTF-8'), '<!DOCTYPE html>                        <html>                            <head>                                <meta charset="UTF-8" />                                <meta http-equiv="refresh" content="1;url=[url]" />                                <title>Redirecting to [url]</title>                            </head>                            <body>                                Redirecting to <a href="[url]">[url]</a>.                            </body>                        </html>');        $this->setStatusCode($statusCode);        $this->setHeader('Location: ' . $url);        $this->setContent($content);    }}