<?phpnamespace Neoflow\CMS\Handler;class Translator extends \Neoflow\Framework\Handler\Translator{    /**     * Load translation.     *     * @return self     */    public function loadTranslation()    {        parent::loadTranslation();        // Load translation files of modules        $modules = $this->app()->get('modules');        foreach ($modules as $module) {            // Load translation file            $translationFile = $module->getPath('/i18n/' . $this->getCurrentLanguageCode() . '.php');            $this->runTranslationFile($translationFile);            // Load fallback translation file            $fallbackTranslationFile = $module->getPath('/i18n/' . $this->getDefaultLanguageCode() . '.php');            $this->runTranslationFile($fallbackTranslationFile, true);        }        return $this;    }}