<?phpnamespace Neoflow\Framework\Handler;use Neoflow\Framework\Common\Container;class Config extends Container{    /**     * Get url.     *     * @param string $uri     *     * @return string     */    public function getUrl($uri = '')    {        return $this->get('url') . rtrim(preg_replace('/[\/]+/', '/', $uri), '/');    }    /**     * Get path.     *     * @param string $uri     *     * @return string     */    public function getPath($uri = '')    {        return $this->get('path') . rtrim(preg_replace('/[\\|\/|\\\\|\/\/]+/', DIRECTORY_SEPARATOR, $uri), DIRECTORY_SEPARATOR);    }    /**     * Get temp folder path     *     * @param string $uri     *     * @return string     */    public function getTempPath($uri = '')    {        return $this->getPath('/temp/' . $uri);    }}